import React, { useState } from "react";
import {
  Modal,
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Image,
  TextInput,
  ScrollView,
} from "react-native";
import { Ionicons } from "@expo/vector-icons/";
import { PrefixTextField } from "../../custom/Text";
import { SafeAreaView } from "react-native-safe-area-context";
import Requesting from "./Requesting";
import Transactions from "./Transactions";
import { useFonts } from "expo-font";

const RequestFundds = ({ closeRequestModal, requestmodalVisible }) => {
  useFonts({
    "ClashGrotesk-Bold": require("../../assets/fonts/ClashGrotesk-Bold.ttf"),
  });
  const [requestingModal, setRequestingModal] = useState(false);
  const [transactionsModal, setTransactionsModdal] = useState(false);
  const requestingopenModal = () => {
    setRequestingModal(true);
  };

  const requestingcloseModal = () => {
    closeRequestModal();
    setRequestingModal(false);
  };
  const openTransactionModal = () => {
    setTransactionsModdal(true);
  };

  const closeTransactionModal = () => {
    setTransactionsModdal(false);
  };
  const [txt, setTxt] = useState("");
  return (
    <>
      <SafeAreaView />
      <View
        style={{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: "blue",
        }}
      >
        <Modal
          animationType="slide"
          transparent={true}
          visible={requestmodalVisible}
          onRequestClose={requestingcloseModal}
        >
          <View
            style={{
              flex: 1,
              justifyContent: "center",
              alignItems: "center",
              backgroundColor: "rgba(0, 0, 0, 0.5)",
              // width: "100%",
            }}
          >
            <View
              style={{
                backgroundColor: "white",
                padding: 30,
                paddingLeft: 70,
                paddingRight: 70,
                justifyContent: "center",
              }}
            >
              <View
                style={{
                  backgroundColor: "white",
                  // padding: 20,
                  borderRadius: 10,
                  // width: 356,
                  display: "flex",
                  flexDirection: "row",
                  alignItems: "center",
                  gap: 7,
                  justifyContent: "space-between",
                }}
              >
                <View
                  style={{
                    display: "flex",
                    flexDirection: "row",
                    alignItems: "center",
                    gap: 7,
                  }}
                >
                  <Text
                    style={{
                      color: "#171717",
                      fontSize: 24,
                      fontFamily: "ClashGrotesk-Bold",
                    }}
                  >
                    🤑
                  </Text>
